import React from "react"
import ProductTableRow from "./ProductTableRow"

const ProductTable = (props) => {

  handleFavorite = (example) => {
    const isFavorite = example.favorite || false
    return (
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center" }}>
        <i
          className={isFavorite ? "fa-solid fa-heart" : "fa-regular fa-heart"}
          style={{
            color: isFavorite ? "#e74c3c" : "#aaa",
            cursor: "pointer",
            fontSize: "1.6rem"
          }}
          onClick={() => this.handleFavoriteClick(example, !isFavorite)}
          title={isFavorite ? "Remove from favorites" : "Add to favorites"}
        ></i>
      </div>
    )
  }
    return (
        <table className="table">
            <thead>
            <tr>
                <th>{handleFavorite}</th>
                <th>Name</th>
                <th>Category</th>
                <th>Energy Rating</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Add</th>
                {/* <th>Delete</th> */}
            </tr>
            </thead>

            <tbody>
            {props.products.map((product) => (
                <ProductTableRow key={product._id} product={product} />
            ))}
            </tbody>
        </table>
    )
}

export default ProductTable
